import shortid from 'shortid';
import objectAssign from 'object-assign';
/**
 * A simple way to provide a unique id to objects
 * Calling multiple times on the same object won't regenerate the __id
 * @param  {Object} the object to identify
 * @return {Object} a __id key identified new object
 */
export default (obj) => objectAssign({ __id: shortid.generate() }, obj);
